import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import MovieList from './../components/MovieList';
import { Container, CircularProgress } from '@mui/material';
import InfiniteScroll from 'react-infinite-scroll-component';
import { useAuth } from '../contexts/AuthContext';

function PopularMoviesPage() {
  const [popularMovies, setPopularMovies] = useState([]);
  const [loading, setLoading] = useState(true);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);
  const { userId } = useAuth();
  const wrapperRef = useRef(null);

  const fetchMovies = async () => {
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/movies/top100movies?page=${page}&limit=20`);
      const movies = response.data.movies;

      if (movies.length === 0) {
        setHasMore(false);
      } else {
        setPopularMovies((prevMovies) => [...prevMovies, ...movies]);
      }
    } catch (error) {
      alert('Error fetching movies: ' + error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchMovies();
  }, [page]);

  const loadMoreMovies = () => {
    setPage((prevPage) => prevPage + 1);
  };

  return (
    <div ref={wrapperRef} className="wrapper">
      <Container
        style={{
          height: '100vh',
          overflow: 'hidden', // Rimuovi overflowY per nascondere la scrollbar principale
        }}
        sx={{
          '&::-webkit-scrollbar': {
            display: 'none',
          },
        }}
      >
        <InfiniteScroll
          dataLength={popularMovies.length}
          next={loadMoreMovies}
          hasMore={hasMore}
          loader={<CircularProgress />}
          endMessage={
            <p style={{ textAlign: 'center' }}>
              <b>You have seen it all!</b>
            </p>
          }
          scrollableTarget="scrollableDiv" // Aggiungi questa linea
          style={{ height: '100vh', overflowY: 'auto' }} // Aggiungi stile per altezza e overflow
        >
          <MovieList title="Popular Movies" movies={popularMovies} />
        </InfiniteScroll>
      </Container>
    </div>
  );
}

export default PopularMoviesPage;
