import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieList from './../components/MovieList';
import { Container, CircularProgress } from '@mui/material';
import InfiniteScroll from 'react-infinite-scroll-component';

function PopularMoviesPage() {
  const [popularMovies, setPopularMovies] = useState([]);
  const [page, setPage] = useState(1);
  const [hasMore, setHasMore] = useState(true);

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/movies/top100movies?page=${page}&limit=6`);
      const movies = response.data;
      setPopularMovies((prevMovies) => [...prevMovies, ...movies]);
      if (movies.length < 6) {
        setHasMore(false);
      }
    } catch (error) {
      alert('Error fetching movies: ' + error);
    }
  };

  const fetchMoreMovies = () => {
    setPage((prevPage) => prevPage + 1);
    fetchMovies();
  };

  return (
    <Container>
      <InfiniteScroll
        dataLength={popularMovies.length}
        next={fetchMoreMovies}
        hasMore={hasMore}
        loader={<CircularProgress />}
        endMessage={<p style={{ textAlign: 'center' }}>Yay! You have seen it all</p>}
      >
        <MovieList title="Popular Movies" movies={popularMovies} />
      </InfiniteScroll>
    </Container>
  );
}

export default PopularMoviesPage;







