import React, { useState, useEffect } from 'react';
import { Container, Typography, Tabs, Tab, Grid, Card, CardMedia, CardContent } from '@mui/material';
import { makeStyles } from '@mui/styles';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
    backgroundColor: theme.palette.background.paper,
  },
  media: {
    height: 140,
  },
  card: {
    margin: theme.spacing(2),
  },
}));

function ViewedMediaPage() {
  const classes = useStyles();
  const { userId } = useAuth();
  const [value, setValue] = useState(0);
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);

  useEffect(() => {
    axios.post('http://localhost:5000/api/media/getViewedMovies', { userId })
      .then(response => {
        if (response.data.success) {
          setMovies(response.data.movies);
        } else {
          console.error('Failed to fetch viewed movies:', response.data.message);
        }
      })
      .catch(error => {
        console.error('Error fetching viewed movies:', error);
      });

    axios.post('http://localhost:5000/api/media/getViewedSeries', { userId })
      .then(response => {
        if (response.data.success) {
          setSeries(response.data.series);
        } else {
          console.error('Failed to fetch viewed series:', response.data.message);
        }
      })
      .catch(error => {
        console.error('Error fetching viewed series:', error);
      });
  }, [userId]);

  const handleChange = (event, newValue) => {
    setValue(newValue);
  };

  return (
    <Container className={classes.root}>
      <Typography variant="h4" gutterBottom>
        Viewed Media
      </Typography>
      <Tabs value={value} onChange={handleChange} aria-label="viewed media tabs">
        <Tab label="Movies" />
        <Tab label="Series" />
      </Tabs>
      <Grid container spacing={3}>
        {value === 0 && movies.map(movie => (
          <Grid item xs={12} sm={6} md={4} key={movie.id}>
            <Card className={classes.card}>
              <CardMedia
                className={classes.media}
                image={`http://localhost:5000/uploads/movie_posters/${movie.poster}`}
                title={movie.title}
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                  {movie.title}
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  {movie.description}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
        {value === 1 && series.map(serie => (
          <Grid item xs={12} sm={6} md={4} key={serie.id}>
            <Card className={classes.card}>
              <CardMedia
                className={classes.media}
                image={`http://localhost:5000/uploads/series_posters/${serie.poster}`}
                title={serie.title}
              />
              <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                  {serie.title}
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  {serie.description}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default ViewedMediaPage;
