// src/components/MediaSearch.js

import React, { useState } from 'react';
import axios from 'axios';
import MovieItem from './MovieItem';
import SerieItem from './SerieItem';
import { Box, CircularProgress, Grid } from '@mui/material';
import '../index.css';

function MediaSearch() {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSearch = async () => {
    if (!searchTerm) return;
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/recommendation/search?query=${searchTerm}`);
      setResults(response.data);
    } catch (error) {
      console.error('Error fetching search results:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'center', margin: '20px' }}>
        <div className="Message">
          <input
            title="Write Message"
            tabIndex="0"
            pattern="\d+"
            placeholder="Search for a movie or series..."
            className="MsgInput"
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSearch()} // Allow search on Enter key
          />
          <svg
            xmlns="http://www.w3.org/2000/svg"
            version="1.0"
            width="30.000000pt"
            height="30.000000pt"
            viewBox="0 0 30.000000 30.000000"
            preserveAspectRatio="xMidYMid meet"
            className="SendSVG"
            onClick={handleSearch} // Trigger search on click
          >
            <g
              transform="translate(0.000000,30.000000) scale(0.100000,-0.100000)"
              fill="#ffffff70"
              stroke="none"
            >
              <path d="M44 256 c-3 -8 -4 -29 -2 -48 3 -31 5 -33 56 -42 28 -5 52 -13 52 -16 0 -3 -24 -11 -52 -16 -52 -9 -53 -9 -56 -48 -2 -21 1 -43 6 -48 10 -10 232 97 232 112 0 7 -211 120 -224 120 -4 0 -9 -6 -12 -14z"></path>
            </g>
          </svg>
          <span className="l"></span>
        </div>
      </Box>
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', margin: '20px' }}>
          <CircularProgress />
        </Box>
      ) : (
        <Grid container spacing={3}>
          {results.map((item) => (
            <Grid item xs={12} sm={6} md={4} key={item.imdbID}>
              {item.type === 'movie' ? <MovieItem movie={item} /> : <SerieItem serie={item} />}
            </Grid>
          ))}
        </Grid>
      )}
    </Box>
  );
}

export default MediaSearch;
