// src/theme.js

import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#D32F2F', // A rich red color for primary elements
    },
    secondary: {
      main: '#FFA000', // An orange color for secondary elements
    },
    background: {
      default: '#1C1C1C', // Dark background for the entire app
      paper: '#424242', // Darker background for paper elements
    },
    text: {
      primary: '#FFFFFF', // White text for readability
    },
  },
  typography: {
    allVariants: {
      color: '#FFFFFF', // Ensure all text is white by default
    },
    h4: {
      fontWeight: 'bold', // Make headers bold
      fontFamily: 'Poppins, sans-serif', // Use a modern, clean font
    },
    body2: {
      fontFamily: 'Poppins, sans-serif', // Use the same font for body text
    },
  },
});

export default theme;
