import React, { useState, useEffect } from 'react';
import axios from 'axios';
import MovieList from './../components/MovieList';
import { Container, CircularProgress } from '@mui/material';

function PopularMoviesPage() {
  const [popularMovies, setPopularMovies] = useState([]);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [hasMore, setHasMore] = useState(true);

  useEffect(() => {
    // This will ensure the effect runs only when `page` changes
    const fetchMovies = async () => {
      if (loading || !hasMore) return;  // Prevent fetch if already loading or no more movies
      setLoading(true);

      try {
        console.log(`Fetching page: ${page}`);  // Debug log
        const response = await axios.get(`http://localhost:5000/api/movies/top100movies?page=${page}&limit=6`);
        const movies = response.data;

        // Check for duplicate IDs
        const newMovies = movies.filter(movie => !popularMovies.some(existingMovie => existingMovie.id === movie.id));

        console.log(`Fetched movies:`, newMovies);  // Debug log
        setPopularMovies((prevMovies) => [...prevMovies, ...newMovies]);

        // If the length of movies fetched is less than limit, it means no more movies to fetch
        if (movies.length < 6) {
          setHasMore(false);
        }
      } catch (error) {
        alert('Error fetching movies: ' + error);
      } finally {
        setLoading(false);
      }
    };

    fetchMovies();
  }, [page, hasMore, loading, popularMovies]);  // Ensure dependencies are correctly set

  useEffect(() => {
    const handleScroll = () => {
      if (window.innerHeight + window.scrollY >= document.body.offsetHeight - 500 && !loading && hasMore) {
        setPage((prevPage) => prevPage + 1);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [loading, hasMore]);

  return (
    <Container>
      <MovieList title="Popular Movies" movies={popularMovies} />
      {loading && <CircularProgress />}
      {!hasMore && <p style={{ textAlign: 'center' }}>Yay! You have seen it all</p>}
    </Container>
  );
}

export default PopularMoviesPage;




