import React, { useState } from 'react';
import { Container, TextField, Button, Typography, List, ListItem, ListItemAvatar, ListItemText, Avatar, Divider } from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

function UserSearchPage() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSearch = () => {
    setLoading(true);
    setError('');

    // Send the search query to the server
    axios.post('http://localhost:5000/api/recommendation/search', { query })
      .then(response => {
        if (response.data.success) {
          setResults(response.data.users);
        } else {
          setError('Failed to fetch search results.');
        }
      })
      .catch(error => {
        console.error('Error fetching search results:', error);
        setError('An error occurred while fetching search results.');
      })
      .finally(() => {
        setLoading(false);
      });
  };

  const handleUserClick = (userId) => {
    // Navigate to the user's account page
    navigate(`/account/${userId}`);
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Search Users
      </Typography>
      <TextField
        label="Search Users"
        variant="outlined"
        fullWidth
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        sx={{ marginBottom: 2 }}
      />
      <Button variant="contained" color="primary" onClick={handleSearch} disabled={loading}>
        {loading ? 'Searching...' : 'Search'}
      </Button>
      {error && <Typography color="error" sx={{ marginTop: 2 }}>{error}</Typography>}
      <List>
        {results.map(user => (
          <div key={user.userId}>
            <ListItem button onClick={() => handleUserClick(user.userId)}>
              <ListItemAvatar>
                <Avatar src={`http://localhost:5000/uploads/profile_images/${user.profileimguri}`} />
              </ListItemAvatar>
              <ListItemText
                primary={
                  <Typography variant="h6" sx={{ fontWeight: 'bold' }}>
                    {user.username}
                  </Typography>
                }
                secondary={
                  <Typography variant="body2" sx={{ color: 'rgba(255, 255, 255, 0.7)' }}>
                    {user.email}
                  </Typography>
                }
              />
            </ListItem>
            <Divider />
          </div>
        ))}
      </List>
    </Container>
  );
}

export default UserSearchPage;
