// src/components/AnimatedBackground.js
import React from 'react';
import { makeStyles } from '@mui/styles';

const useStyles = makeStyles(() => ({
  root: {
    position: 'fixed',
    top: 0,
    left: 0,
    width: '100%',
    height: '100%',
    zIndex: -1, // ensures the background is behind all content
    overflow: 'hidden',
    backgroundColor: '#000', // base background color
  },
  circle: {
    position: 'absolute',
    borderRadius: '50%',
    background: 'rgba(255, 255, 255, 0.1)',
    animation: '$move 20s linear infinite',
  },
  '@keyframes move': {
    '0%': {
      transform: 'scale(0) translateY(0) translateX(0)',
    },
    '50%': {
      transform: 'scale(1) translateY(200px) translateX(200px)',
    },
    '100%': {
      transform: 'scale(0) translateY(400px) translateX(400px)',
    },
  },
}));

const AnimatedBackground = () => {
  const classes = useStyles();

  return (
    <div className={classes.root}>
      <div
        className={classes.circle}
        style={{
          width: '400px',
          height: '400px',
          top: '10%',
          left: '20%',
        }}
      ></div>
      <div
        className={classes.circle}
        style={{
          width: '300px',
          height: '300px',
          top: '50%',
          left: '70%',
        }}
      ></div>
      <div
        className={classes.circle}
        style={{
          width: '500px',
          height: '500px',
          top: '70%',
          left: '30%',
        }}
      ></div>
    </div>
  );
};

export default AnimatedBackground;
