import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Box, Typography, TextField, Button } from '@mui/material';
import { useAuth } from '../contexts/AuthContext';
import SendIcon from '@mui/icons-material/Send';
import { useNavigate } from 'react-router-dom';

function CommentsSection({ mediaId, type }) {
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState('');
  const { userId } = useAuth();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchComments = async () => {
      try {
        const response = await axios.post('http://localhost:5000/api/comments/getComments', { movie_id: mediaId });
        if (response.data.success) {
          const commentsWithUsernames = await Promise.all(response.data.comments_list.map(async (comment) => {
            // Ottieni il nome utente per ogni commento
            const usernameResponse = await axios.post('http://localhost:5000/api/comments/getUsernameFromId', { userId: comment.user_id });
            return {
              ...comment,
              username: usernameResponse.data.success ? usernameResponse.data.username : comment.user_id // Usa l'ID utente se il nome utente non è disponibile
            };
          }));
          setComments(commentsWithUsernames);
        } else {
          console.error('Error fetching comments:', response.data.message);
        }
      } catch (error) {
        console.error('Error fetching comments:', error);
      }
    };

    fetchComments();
  }, [mediaId]);

  const handleCommentSubmit = async () => {
    if (userId === -1) {
      navigate("/signin");
    } else {
      try {
        const response = await axios.post('http://localhost:5000/api/comments/addComment', {
          userId,
          movieId: mediaId,
          comment: newComment
        });
        if (response.data.success) {
          const usernameResponse = await axios.post('http://localhost:5000/api/comments/getUsernameFromId', { userId });
          const newCommentWithUsername = {
            ...response.data.comment,
            username: usernameResponse.data.success ? usernameResponse.data.username : userId
          };
          setComments([...comments, newCommentWithUsername]);
          setNewComment('');
        } else {
          alert('Error adding comment: ' + response.data.message);
        }
      } catch (error) {
        alert('Error adding comment: ' + error);
      }
    }
  };

  return (
    <Box sx={{ mt: 2 }}>
      <Typography variant="h6">Comments</Typography>
      <Box sx={{ mt: 1 }}>
        {comments.map((comment) => (
          <Box key={comment.id} sx={{ mb: 1 }}>
            <Typography variant="body1">{comment.username}: {comment.comment}</Typography>
          </Box>
        ))}
      </Box>
      <Box sx={{ display: 'flex', mt: 2 }}>
        <TextField
          value={newComment}
          onChange={(e) => setNewComment(e.target.value)}
          label="Add a comment"
          fullWidth
        />
        <Button
          onClick={handleCommentSubmit}
          startIcon={<SendIcon />}
          sx={{
            ml: 1,
            color: "#fff",
            border: "solid 1px #1E1E1E",
            '&:hover': {
              borderColor: "#fff",
            }
          }}
        />
      </Box>
    </Box>
  );
}

export default CommentsSection;
