import React, { useState } from 'react';
import { AppBar, Toolbar, Typography, Button, Menu, MenuItem, IconButton } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import useMediaQuery from '@mui/material/useMediaQuery';
import { useTheme } from '@mui/material/styles';

function Navbar() {
  const [anchorEl, setAnchorEl] = useState(null);
  const { isLoggedIn } = useAuth();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm'));

  const handleMenu = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <AppBar position="sticky" sx={{ marginBottom: '16px' }}>
      <Toolbar>
        <IconButton
          edge="start"
          color="inherit"
          aria-label="menu"
          onClick={handleMenu}
          sx={{ mr: 2 }}
        >
          <MenuIcon />
        </IconButton>
        <Typography variant="h6" sx={{ flexGrow: 1 }}>
          CineMate
        </Typography>
        {!isMobile && (
          <>
            <Button color="inherit" component={Link} to="/">Home</Button>
            <Button color="inherit" component={Link} to="/search">Search Media</Button>
            <Button color="inherit" component={Link} to="/popularMovies">Popular Movies</Button>
            <Button color="inherit" component={Link} to="/popularSeries">Popular Series</Button>
            <Button color="inherit" component={Link} to={isLoggedIn ? "/account" : "/signin"}>Account</Button>
          </>
        )}
        <Menu
          id="menu-appbar"
          anchorEl={anchorEl}
          anchorOrigin={{
            vertical: 'top',
            horizontal: 'right',
          }}
          keepMounted
          transformOrigin={{
            vertical: 'top',
            horizontal: 'right',
          }}
          open={Boolean(anchorEl)}
          onClose={handleClose}
        >
          <MenuItem onClick={handleClose} component={Link} to="/">Home</MenuItem>
          <MenuItem onClick={handleClose} component={Link} to="/search">Search Media</MenuItem>
          <MenuItem onClick={handleClose} component={Link} to="/popularMovies">Popular Movies</MenuItem>
          <MenuItem onClick={handleClose} component={Link} to="/popularSeries">Popular Series</MenuItem>
          <MenuItem onClick={handleClose} component={Link} to={isLoggedIn ? "/account" : "/signin"}>Account</MenuItem>
        </Menu>
      </Toolbar>
    </AppBar>
  );
}

export default Navbar;
