import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Avatar,
  Grid,
  Button,
  TextField,
  MenuItem,
  Select,
  FormControl,
  InputLabel,
} from '@mui/material';
import { makeStyles } from '@mui/styles';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../contexts/AuthContext';

// Define styles with makeStyles
const useStyles = makeStyles((theme) => ({
  container: {
    backgroundColor: '#121212', // Dark background color
    color: '#FFFFFF', // White text color
    padding: theme.spacing(4),
    borderRadius: theme.spacing(2),
  },
  primaryText: {
    color: '#FFFFFF',
    fontWeight: 'bold',
  },
  avatar: {
    width: theme.spacing(15),
    height: theme.spacing(15),
    marginBottom: theme.spacing(2),
  },
  inputContainer: {
    marginBottom: theme.spacing(3),
  },
  button: {
    marginTop: theme.spacing(3),
  },
  textField: {
    '& .MuiInputBase-root': {
      color: '#FFFFFF',
      backgroundColor: '#333333', // Background for input fields
    },
    '& .MuiInputLabel-root': {
      color: '#AAAAAA',
    },
    '& .MuiOutlinedInput-root': {
      '& fieldset': {
        borderColor: '#555555',
      },
      '&:hover fieldset': {
        borderColor: '#FFFFFF',
      },
      '&.Mui-focused fieldset': {
        borderColor: '#FFFFFF',
      },
      '&.Mui-disabled': {
        backgroundColor: '#555555', // Lighter background for disabled inputs
        color: '#CCCCCC',
      },
      '&.Mui-disabled fieldset': {
        borderColor: '#777777',
      },
    },
  },
  select: {
    '& .MuiSelect-icon': {
      color: '#FFFFFF', // Color of the select arrow
    },
  },
}));

function AccountPage() {
  const classes = useStyles();
  const { logout, userId: authUserId } = useAuth();
  const navigate = useNavigate();
  const { userId: routeUserId } = useParams();
  const isOwnProfile = !routeUserId || routeUserId === authUserId;
  const [userInfo, setUserInfo] = useState({
    userId: isOwnProfile ? authUserId : routeUserId,
    username: '',
    email: '',
    gender: '',
    birthday: '',
    language: '',
    profileimguri: null,
    description: '',
  });
  const [birthdayError, setBirthdayError] = useState('');
  const [imageFile, setImageFile] = useState(null);

  useEffect(() => {
    const currentUserId = isOwnProfile ? authUserId : routeUserId;
    axios
      .post('http://localhost:5000/api/auth/getUserInfo', { userId: currentUserId })
      .then((response) => {
        if (response.data.success) {
          const userData = response.data.message;
          userData.birthday = new Date(userData.birthday).toISOString().split('T')[0];
          setUserInfo(userData);
        } else {
          console.error('Failed to fetch user info:', response.data.message);
        }
      })
      .catch((error) => {
        console.error('Error fetching user info:', error);
      });
  }, [authUserId, routeUserId, isOwnProfile]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === 'birthday') {
      const date = new Date(value);
      if (isNaN(date)) {
        setBirthdayError('Invalid date');
      } else {
        setBirthdayError('');
      }
    }
    setUserInfo({
      ...userInfo,
      [name]: value,
    });
  };

  const handleFileChange = (e) => {
    setImageFile(e.target.files[0]);
  };

  const handleImageUpload = async () => {
    if (!imageFile) {
      alert('Please select an image file to upload.');
      return;
    }

    const formData = new FormData();
    formData.append('profileImage', imageFile);
    formData.append('userId', userInfo.userId);

    try {
      const response = await axios.post('http://localhost:5000/api/auth/uploadProfileImage', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.success) {
        setUserInfo({
          ...userInfo,
          profileimguri: response.data.imageUrl,
        });
        alert('Profile image updated successfully');
      } else {
        console.error('Failed to upload image:', response.data.message);
        alert(`Failed to upload image: ${response.data.message}`);
      }
    } catch (error) {
      console.error('Error uploading image:', error);
      alert('An error occurred while uploading the image');
    }
  };

  const handleSave = () => {
    if (birthdayError) {
      return;
    }
    axios
      .post('http://localhost:5000/api/auth/updateUserInfo', userInfo)
      .then((response) => {
        if (response.data.success) {
          alert('User information updated successfully');
        } else {
          console.error('Failed to update user info:', response.data.message);
        }
      })
      .catch((error) => {
        console.error('Error updating user info:', error);
        alert('An error occurred while updating the information');
      });
  };

  const handleSignout = () => {
    logout();
    navigate('/signin');
  };

  const handleViewMedia = () => {
    if (isOwnProfile) {
      navigate('/viewedMedia');
    } else {
      navigate(`/viewedMedia/${routeUserId}`);
    }
  };

  return (
    <Container className={classes.container}>
      <Typography variant="h4" gutterBottom className={classes.primaryText}>
        Account Information
      </Typography>
      <Grid container spacing={3}>
        <Grid item xs={12} sm={4}>
          <Avatar
            src={
              userInfo.profileimguri
                ? `http://localhost:5000/uploads/profile_images/${userInfo.profileimguri}`
                : undefined
            }
            alt={`${userInfo.username}'s profile`}
            className={classes.avatar}
          />
          {isOwnProfile && (
            <>
              <input
                accept="image/*"
                type="file"
                onChange={handleFileChange}
                style={{ display: 'block', marginTop: '10px', color: '#FFFFFF' }}
              />
              <Button
                variant="contained"
                color="primary"
                onClick={handleImageUpload}
                className={classes.button}
                sx={{ margin: '10px', marginBottom: '30px' }}
                disabled={!imageFile}
              >
                Upload Image
              </Button>
            </>
          )}
        </Grid>
        <Grid item xs={12} sm={8}>
          <div className={classes.inputContainer}>
            <TextField
              label="Username"
              variant="outlined"
              fullWidth
              name="username"
              value={userInfo.username}
              onChange={handleChange}
              className={classes.textField}
              disabled={!isOwnProfile}
            />
          </div>
          <div className={classes.inputContainer}>
            <TextField
              label="Email"
              variant="outlined"
              fullWidth
              name="email"
              value={userInfo.email}
              onChange={handleChange}
              className={classes.textField}
              disabled={!isOwnProfile}
            />
          </div>
          <div className={classes.inputContainer}>
            <FormControl variant="outlined" fullWidth className={classes.textField}>
              <InputLabel>Gender</InputLabel>
              <Select
                name="gender"
                value={userInfo.gender}
                onChange={handleChange}
                className={classes.select}
                disabled={!isOwnProfile}
                label="Gender"
              >
                <MenuItem value="M">Male</MenuItem>
                <MenuItem value="F">Female</MenuItem>
              </Select>
            </FormControl>
          </div>
          <div className={classes.inputContainer}>
            <TextField
              label="Birthday"
              variant="outlined"
              fullWidth
              name="birthday"
              type="date"
              value={userInfo.birthday}
              onChange={handleChange}
              className={classes.textField}
              error={Boolean(birthdayError)}
              helperText={birthdayError}
              disabled={!isOwnProfile}
              InputLabelProps={{
                shrink: true,
              }}
            />
          </div>
          <div className={classes.inputContainer}>
            <FormControl variant="outlined" fullWidth className={classes.textField}>
              <InputLabel>Language</InputLabel>
              <Select
                name="language"
                value={userInfo.language}
                onChange={handleChange}
                className={classes.select}
                disabled={!isOwnProfile}
                label="Language"
              >
                <MenuItem value="ita">Italian</MenuItem>
                <MenuItem value="eng">English</MenuItem>
                <MenuItem value="esp">Spanish</MenuItem>
                <MenuItem value="fra">French</MenuItem>
                <MenuItem value="deu">German</MenuItem>
              </Select>
            </FormControl>
          </div>
          <div className={classes.inputContainer}>
            <TextField
              label="Description"
              variant="outlined"
              fullWidth
              name="description"
              value={userInfo.description}
              onChange={handleChange}
              className={classes.textField}
              multiline
              rows={4}
              disabled={!isOwnProfile}
            />
          </div>
          {isOwnProfile && (
            <div className={classes.inputContainer}>
              <Button
                variant="contained"
                color="primary"
                onClick={handleViewMedia}
                className={classes.button}
                sx={{ margin: '10px', marginBottom: '30px' }}
              >
                Viewed Movies and Series
              </Button>
            </div>
          )}
        </Grid>
      </Grid>
      {isOwnProfile ? (
        <>
          <Button
            variant="contained"
            color="primary"
            onClick={handleSave}
            className={classes.button}
            sx={{ margin: '10px', marginBottom: '30px' }}
          >
            Save Changes
          </Button>
          <Button
            variant="contained"
            color="primary"
            onClick={handleSignout}
            className={classes.button}
            sx={{ margin: '10px', marginBottom: '30px' }}
          >
            Sign Out
          </Button>
        </>
      ) : (
        <Button
          variant="contained"
          color="primary"
          className={classes.button}
          sx={{ margin: '10px', marginBottom: '30px' }}
          onClick={() => navigate('/')}
        >
          Back to Users List
        </Button>
      )}
    </Container>
  );
}

export default AccountPage;
