import React, { useState } from 'react';
import axios from 'axios';
import MovieItem from './MovieItem';
import SerieItem from './SerieItem';
import { TextField, IconButton, Box, CircularProgress, Grid } from '@mui/material';
import SearchIcon from '@mui/icons-material/Search';

function MediaSearch() {
  const [searchTerm, setSearchTerm] = useState('');
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleSearch = async () => {
    if (!searchTerm) return;
    setLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/search?query=${searchTerm}`);
      setResults(response.data);
    } catch (error) {
      console.error('Error fetching search results:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <Box>
      <Box sx={{ display: 'flex', justifyContent: 'center', margin: '20px' }}>
        <TextField
          label="Search for a movie or series"
          variant="outlined"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <IconButton onClick={handleSearch}>
          <SearchIcon />
        </IconButton>
      </Box>
      {loading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', margin: '20px' }}>
          <CircularProgress />
        </Box>
      ) : (
        <Grid container spacing={3}>
          {results.map((item) => (
            <Grid item xs={12} sm={6} md={4} key={item.imdbID}>
              {item.Type === 'movie' ? <MovieItem movie={item} /> : <SerieItem serie={item} />}
            </Grid>
          ))}
        </Grid>
      )}
    </Box>
  );
}

export default MediaSearch;
